sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...

  // test record
  var gr_invoice = new GlideRecord('x_snc_rpachallen_0_invoice');
  var record_id = 'd15184671b31911013de99f1b24bcbb7';

  // 1. Working code ...
  gr_invoice.get(record_id);
  gr_invoice.setValue('total_price', 'CHF;2500');
  gr_invoice.update();  

  gr_invoice.get(record_id);
  gs.info('1. Total price has been updated to: ' + gr_invoice.getValue('total_price'));

  // 2. Still working code (Value dynamic) 
  gr_invoice.get(record_id);
  gr_invoice.setValue('total_price', 'CHF;' + inputs.value);
  gr_invoice.update();  

  gr_invoice.get(record_id);
  gs.info('2. Total price has been updated to: ' + gr_invoice.getValue('total_price'));

  // 3. NOT working code (Currency dynamic)
  gr_invoice.get(record_id);
  gr_invoice.setValue('total_price', '' + inputs.currency + ';2500');
  gr_invoice.update();  

  gr_invoice.get(record_id);
  gs.info('3. Total price has been updated to: ' + gr_invoice.getValue('total_price'));


  // 4. NOT working code (Field dynamic) 
  gr_invoice.get(record_id);
  gr_invoice.setValue(inputs.field, 'CHF;2500');
  gr_invoice.update();  

  gr_invoice.get(record_id);
  gs.info('4. Total price has been updated to: ' + gr_invoice.getValue('total_price'));


//  var target_field = inputs.field + '';
